insert  into `shd_email_templates`(`type`,`name`,`name_en`,`subject`,`message`,`attachments`,`fromname`,`fromemail`,`disabled`,`custom`,`language`,`copyto`,`blind_copy_to`,`plaintext`,`create_time`,`update_time`) values ('invoice','信用额账单已生成','Credit_Invoice','{SYSTEM_COMPANYNAME}信用额账单已生成','&lt;!DOCTYPE html&gt;\n&lt;html xmlns=&quot;http://www.w3.org/1999/xhtml&quot;&gt;\n\n&lt;head&gt;\n  &lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html; charset=UTF-8&quot; /&gt;\n  &lt;meta name=&quot;viewport&quot; content=&quot;width=device-width, initial-scale=1.0&quot; /&gt;\n&lt;/head&gt;\n\n&lt;body style=&quot;margin: 0; padding: 0;&quot;&gt;\n&lt;table style=&quot;height: 100vh;&quot; width=&quot;100%&quot; cellspacing=&quot;0&quot; cellpadding=&quot;0&quot;&gt;\n&lt;tbody&gt;\n&lt;tr&gt;\n&lt;td style=&quot;position: relative;&quot;&gt;\n&lt;table style=&quot;margin: 0px; padding: 0px; border-collapse: collapse; width: 70vw; height: 100vh; position: absolute; top: 0; left: 15%;&quot; cellspacing=&quot;0&quot; cellpadding=&quot;0&quot; align=&quot;center&quot;&gt;\n&lt;tbody&gt;\n&lt;tr&gt;\n&lt;td style=&quot;margin: 0px; padding: 0px; position: relative; width: 100%; height: 360px; background-color: #142467;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; display: inline-block; position: absolute; bottom: 0; margin-left: 2%; width: 98%; height: 50px; background-color: #007bfc; box-sizing: border-box;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/td&gt;\n&lt;/tr&gt;\n&lt;tr&gt;\n&lt;td style=&quot;margin: 0px; padding: 0px; position: relative; flex: 1 1 0%; height: 506px;&quot;&gt;&lt;span style=&quot;margin: 0px; padding: 0px; display: inline-block; position: absolute; bottom: 0; left: 50%; width: 50%; height: 50px; background-color: #007bfc; box-sizing: border-box;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/td&gt;\n&lt;/tr&gt;\n&lt;tr&gt;\n&lt;td style=&quot;margin: 0px; padding: 0px; position: relative; width: 100%; height: 80px; background-color: #1d1f2e;&quot;&gt;&amp;nbsp;&lt;/td&gt;\n&lt;/tr&gt;\n&lt;/tbody&gt;\n&lt;/table&gt;\n&lt;table style=&quot;margin: 0px; padding: 0px; border-collapse: collapse; width: 60vw; height: 100vh; position: absolute; top: 0; left: 20%;&quot; cellspacing=&quot;0&quot; cellpadding=&quot;0&quot; align=&quot;center&quot;&gt;&lt;!-- 邮件标题 --&gt;\n&lt;tbody&gt;\n&lt;tr style=&quot;margin: 0; padding: 0; height: 160px;&quot;&gt;\n&lt;td style=&quot;margin: 0px; padding: 0px; height: 160px; text-align: center;&quot;&gt;&lt;span style=&quot;margin: 0; padding: 0; display: inline-block; font-size: 32px; width: 128px; font-family: MicrosoftYaHei; letter-spacing: 0px; color: #ffffff; float: left; white-space: nowrap;&quot;&gt;{SYSTEM_COMPANYNAME}&lt;/span&gt; &lt;span style=&quot;margin: 0; padding: 0; display: inline-block; font-family: MicrosoftYaHei; font-size: 24px; letter-spacing: 0px; color: #ffffff;&quot;&gt;[{SYSTEM_COMPANYNAME}]信用额账单待支付&lt;/span&gt; &lt;span style=&quot;margin: 0; padding: 0; display: inline-block; font-size: 32px; width: 128px;&quot;&gt;&amp;nbsp;&lt;/span&gt;&lt;/td&gt;\n&lt;/tr&gt;\n&lt;!-- 邮件内容 --&gt;\n&lt;tr style=&quot;margin: 0; padding: 0; height: 724px;&quot;&gt;\n&lt;td style=&quot;margin: 0px; padding: 50px 5%; font-size: 14px; box-sizing: border-box; min-height: 724px; background: #fff; box-shadow: 0px 0px 8px 0px rgba(117, 117, 117, 0.48); border-radius: 2px; line-height: 27px; color: #333333; vertical-align: initial;&quot;&gt;&lt;span style=&quot;margin: 0; padding: 0; font-family: MicrosoftYaHei; font-size: 18px; line-height: 24px; letter-spacing: 0px; color: #1d1f2e;&quot;&gt; &lt;strong&gt;尊敬的用户{USERNAME}&lt;/strong&gt; &lt;/span&gt;&lt;br /&gt;&lt;br /&gt;&lt;span style=&quot;margin: 0; padding: 0; display: inline-block; margin-top: 55px;&quot;&gt;您有一笔信用额账单已产生，账单号#{INVOICE_ID}，金额{INVOICE_TOTAL_FEE}。&lt;/span&gt;&lt;br /&gt;&lt;span style=&quot;margin: 0; padding: 0; display: inline-block; margin-top: 60px;&quot;&gt;为了避免所关联产品被暂停，请您及时付款。&lt;/span&gt;&